  output <- .rds$stop_db_cluster_output()
  config <- get_config()
  svc <- .rds$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.rds$operations$stop_db_cluster <- rds_stop_db_cluster

