#'
#' @rdname ssm
#' @export
ssm <- function(config = list(), credentials = list(), endpoint = NULL, region = NULL) {
  config <- merge_config(
    config,
    list(
      credentials = credentials,
      endpoint = endpoint,
      region = region
    )
  )
  svc <- .ssm$operations
  svc <- set_config(svc, config)
  return(svc)
}

