#' @param MultiplexId &#91;required&#93; The ID of the multiplex of the program to update.
#' @param MultiplexProgramSettings The new settings for a multiplex program.
#' @param ProgramName &#91;required&#93; The name of the program to update.
#'
#' @section Request syntax:
#' ```
#' svc$update_multiplex_program(
#'   MultiplexId = "string",
#'   MultiplexProgramSettings = list(
#'     ProgramNumber = 123,
#'     ServiceDescriptor = list(
#'       ProviderName = "string",
#'       ServiceName = "string"
#'     ),
#'     VideoSettings = list(
#'       ConstantBitrate = 123,
#'       StatmuxSettings = list(
#'         MaximumBitrate = 123,
#'         MinimumBitrate = 123
#'       )
#'     )
#'   ),
#'   ProgramName = "string"
#' )
#' ```
#'
#' @keywords internal
#'
#' @rdname medialive_update_multiplex_program
medialive_update_multiplex_program <- function(MultiplexId, MultiplexProgramSettings = NULL, ProgramName) {
  op <- new_operation(
    name = "UpdateMultiplexProgram",
    http_method = "PUT",
    http_path = "/prod/multiplexes/{multiplexId}/programs/{programName}",
    paginator = list()
  )
  input <- .medialive$update_multiplex_program_input(MultiplexId = MultiplexId, MultiplexProgramSettings = MultiplexProgramSettings, ProgramName = ProgramName)
  output <- .medialive$update_multiplex_program_output()
  config <- get_config()
  svc <- .medialive$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.medialive$operations$update_multiplex_program <- medialive_update_multiplex_program

#' Update reservation
#'
#' Update reservation.
#'
#' @usage
#' medialive_update_reservation(Name, ReservationId)
#'
#' @param Name Name of the reservation
#' @param ReservationId &#91;required&#93; Unique reservation ID, e.g. '1234567'
#'
#' @section Request syntax:
#' ```
#' svc$update_reservation(
#'   Name = "string",
#'   ReservationId = "string"
#' )
#' ```
#'
#' @keywords internal
#'
#' @rdname medialive_update_reservation
medialive_update_reservation <- function(Name = NULL, ReservationId) {
  op <- new_operation(
    name = "UpdateReservation",
    http_method = "PUT",
    http_path = "/prod/reservations/{reservationId}",
    paginator = list()
  )
  input <- .medialive$update_reservation_input(Name = Name, ReservationId = ReservationId)
  output <- .medialive$update_reservation_output()
  config <- get_config()
  svc <- .medialive$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.medialive$operations$update_reservation <- medialive_update_reservation
