#'       name = "string",
#'       values = list(
#'         "string"
#'       ),
#'       condition = "string"
#'     )
#'   ),
#'   startTime = as.POSIXct(
#'     "2015-01-01"
#'   ),
#'   endTime = as.POSIXct(
