#'
#' @keywords internal
#'
#' @rdname appflow_update_flow
appflow_update_flow <- function(flowName, description = NULL, triggerConfig, sourceFlowConfig = NULL, destinationFlowConfigList, tasks) {
  op <- new_operation(
    name = "UpdateFlow",
    http_method = "POST",
    http_path = "/update-flow",
    paginator = list()
  )
  input <- .appflow$update_flow_input(flowName = flowName, description = description, triggerConfig = triggerConfig, sourceFlowConfig = sourceFlowConfig, destinationFlowConfigList = destinationFlowConfigList, tasks = tasks)
  output <- .appflow$update_flow_output()
  config <- get_config()
  svc <- .appflow$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.appflow$operations$update_flow <- appflow_update_flow
