    paginator = list(),
    stream_api = FALSE
  )
  input <- .arczonalshift$start_zonal_shift_input(awayFrom = awayFrom, comment = comment, expiresIn = expiresIn, resourceIdentifier = resourceIdentifier)
  output <- .arczonalshift$start_zonal_shift_output()
