#'     StorageCapacity = 123,
#'     StorageType = "SSD"|"HDD",
#'     VpcId = "string",
#'     SubnetIds = list(
#'       "string"
#'     ),
#'     NetworkInterfaceIds = list(
#'       "string"
#'     ),
#'     DNSName = "string",
#'     KmsKeyId = "string",
#'     ResourceARN = "string",
#'     Tags = list(
#'       list(
#'         Key = "string",
#'         Value = "string"
#'       )
