#'       AutomaticBackupRetentionDays = 123,
#'       CopyTagsToBackups = TRUE|FALSE,
#'       Aliases = list(
#'         list(
#'           Name = "string",
#'           Lifecycle = "AVAILABLE"|"CREATING"|"DELETING"|"CREATE_FAILED"|"DELETE_FAILED"
#'         )
#'       )
#'     ),
#'     LustreConfiguration = list(
