iotevents_update_detector_model <- function(detectorModelName, detectorModelDefinition, detectorModelDescription = NULL, roleArn, evaluationMethod = NULL) {
  op <- new_operation(
    name = "UpdateDetectorModel",
    http_method = "POST",
    http_path = "/detector-models/{detectorModelName}",
    paginator = list()
  )
  input <- .iotevents$update_detector_model_input(detectorModelName = detectorModelName, detectorModelDefinition = detectorModelDefinition, detectorModelDescription = detectorModelDescription, roleArn = roleArn, evaluationMethod = evaluationMethod)
  output <- .iotevents$update_detector_model_output()
  config <- get_config()
  svc <- .iotevents$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.iotevents$operations$update_detector_model <- iotevents_update_detector_model

#' Updates an input
#'
#' Updates an input.
#'
#' @usage
#' iotevents_update_input(inputName, inputDescription, inputDefinition)
#'
#' @param inputName &#91;required&#93; The name of the input you want to update.
#' @param inputDescription A brief description of the input.
#' @param inputDefinition &#91;required&#93; The definition of the input.
#'
#' @section Request syntax:
#' ```
#' svc$update_input(
#'   inputName = "string",
#'   inputDescription = "string",
#'   inputDefinition = list(
#'     attributes = list(
#'       list(
#'         jsonPath = "string"
#'       )
#'     )
#'   )
#' )
#' ```
#'
#' @keywords internal
#'
#' @rdname iotevents_update_input
iotevents_update_input <- function(inputName, inputDescription = NULL, inputDefinition) {
  op <- new_operation(
    name = "UpdateInput",
    http_method = "PUT",
    http_path = "/inputs/{inputName}",
    paginator = list()
  )
  input <- .iotevents$update_input_input(inputName = inputName, inputDescription = inputDescription, inputDefinition = inputDefinition)
  output <- .iotevents$update_input_output()
  config <- get_config()
  svc <- .iotevents$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.iotevents$operations$update_input <- iotevents_update_input
