#' # The following stop-engagement example stops an engagement from paging
#' # further contacts and contact channels.
#' svc$stop_engagement(
#'   EngagementId = "arn:aws:ssm-contacts:us-east-2:111122223333:engagement/ex..."
#' )
#' }
#'
#' @keywords internal
#'
#' @rdname ssmcontacts_stop_engagement
ssmcontacts_stop_engagement <- function(EngagementId, Reason = NULL) {
  op <- new_operation(
    name = "StopEngagement",
    http_method = "POST",
    http_path = "/",
    paginator = list()
  )
  input <- .ssmcontacts$stop_engagement_input(EngagementId = EngagementId, Reason = Reason)
  output <- .ssmcontacts$stop_engagement_output()
  config <- get_config()
  svc <- .ssmcontacts$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.ssmcontacts$operations$stop_engagement <- ssmcontacts_stop_engagement

#' Tags a contact or escalation plan
#'
#' @description
#' Tags a contact or escalation plan. You can tag only contacts and
#' escalation plans in the first region of your replication set.
#'
#' @usage
#' ssmcontacts_tag_resource(ResourceARN, Tags)
#'
#' @param ResourceARN &#91;required&#93; The Amazon Resource Name (ARN) of the contact or escalation plan.
#' @param Tags &#91;required&#93; A list of tags that you are adding to the contact or escalation plan.
#'
#' @return
#' An empty list.
#'
#' @section Request syntax:
#' ```
#' svc$tag_resource(
#'   ResourceARN = "string",
#'   Tags = list(
#'     list(
#'       Key = "string",
#'       Value = "string"
#'     )
#'   )
#' )
#' ```
#'
#' @examples
#' \dontrun{
#' # The following tag-resource example tags a specified contact with the
#' # provided tag key value pair.
#' svc$tag_resource(
#'   ResourceARN = "arn:aws:ssm-contacts:us-east-1:111122223333:contact/akuam",
#'   Tags = list(
#'     list(
#'       Key = "group1",
#'       Value = "1"
#'     )
#'   )
#' )
#' }
#'
#' @keywords internal
#'
#' @rdname ssmcontacts_tag_resource
ssmcontacts_tag_resource <- function(ResourceARN, Tags) {
  op <- new_operation(
    name = "TagResource",
    http_method = "POST",
    http_path = "/",
    paginator = list()
  )
  input <- .ssmcontacts$tag_resource_input(ResourceARN = ResourceARN, Tags = Tags)
  output <- .ssmcontacts$tag_resource_output()
  config <- get_config()
  svc <- .ssmcontacts$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.ssmcontacts$operations$tag_resource <- ssmcontacts_tag_resource

#' Removes tags from the specified resource
#'
#' @description
#' Removes tags from the specified resource.
#'
#' @usage
