.ssmcontacts$operations$untag_resource <- ssmcontacts_untag_resource

#' Updates the contact or escalation plan specified
#'
#' @description
#' Updates the contact or escalation plan specified.
#'
#' @usage
#' ssmcontacts_update_contact(ContactId, DisplayName, Plan)
#'
#' @param ContactId &#91;required&#93; The Amazon Resource Name (ARN) of the contact or escalation plan you're
#' updating.
#' @param DisplayName The full name of the contact or escalation plan.
#' @param Plan A list of stages. A contact has an engagement plan with stages for
#' specified contact channels. An escalation plan uses these stages to
#' contact specified contacts.
#'
#' @return
#' An empty list.
#'
#' @section Request syntax:
#' ```
#' svc$update_contact(
#'   ContactId = "string",
#'   DisplayName = "string",
#'   Plan = list(
#'     Stages = list(
#'       list(
#'         DurationInMinutes = 123,
#'         Targets = list(
#'           list(
#'             ChannelTargetInfo = list(
#'               ContactChannelId = "string",
#'               RetryIntervalInMinutes = 123
#'             ),
#'             ContactTargetInfo = list(
#'               ContactId = "string",
#'               IsEssential = TRUE|FALSE
#'             )
#'           )
#'         )
#'       )
#'     )
#'   )
#' )
#' ```
#'
#' @examples
#' \dontrun{
#' # The following update-contact example updates the engagement plan of the
#' # contact Akua to include the three types of contacts channels. This is
#' # done after creating contact channels for Akua.
#' svc$update_contact(
#'   ContactId = "arn:aws:ssm-contacts:us-east-2:111122223333:contact/akuam",
#'   Plan = list(
#'     Stages = list(
#'       list(
#'         DurationInMinutes = 5L,
#'         Targets = list(
#'           list(
#'             ChannelTargetInfo = list(
#'               ContactChannelId = "arn:aws:ssm-contacts:us-east-2:1111222233...",
#'               RetryIntervalInMinutes = 1L
#'             )
#'           )
#'         )
#'       ),
#'       list(
#'         DurationInMinutes = 5L,
#'         Targets = list(
#'           list(
#'             ChannelTargetInfo = list(
#'               ContactChannelId = "arn:aws:ssm-contacts:us-east-2:1111222233...",
#'               RetryIntervalInMinutes = 1L
#'             )
#'           )
#'         )
#'       ),
#'       list(
#'         DurationInMinutes = 5L,
#'         Targets = list(
#'           list(
#'             ChannelTargetInfo = list(
#'               ContactChannelId = "arn:aws:ssm-contacts:us-east-2:1111222233...",
#'               RetryIntervalInMinutes = 1L
#'             )
#'           )
#'         )
#'       )
#'     )
#'   )
#' )
#' }
#'
#' @keywords internal
#'
#' @rdname ssmcontacts_update_contact
ssmcontacts_update_contact <- function(ContactId, DisplayName = NULL, Plan = NULL) {
  op <- new_operation(
    name = "UpdateContact",
    http_method = "POST",
    http_path = "/",
    paginator = list()
  )
  input <- .ssmcontacts$update_contact_input(ContactId = ContactId, DisplayName = DisplayName, Plan = Plan)
  output <- .ssmcontacts$update_contact_output()
  config <- get_config()
  svc <- .ssmcontacts$service(config)
  request <- new_request(svc, op, input, output)
  response <- send_request(request)
  return(response)
}
.ssmcontacts$operations$update_contact <- ssmcontacts_update_contact

