#'
#' @param appId &#91;required&#93; Unique Id for an Amplify App.
#' @param domainName &#91;required&#93; Name of the domain.
#' @param enableAutoSubDomain Enables automated creation of Subdomains for branches.
#' @param subDomainSettings &#91;required&#93; Setting structure for the Subdomain.
#'
#' @export
update_domain_association <- function (appId, domainName, enableAutoSubDomain = NULL, 
    subDomainSettings) 
{
    op <- new_operation(name = "UpdateDomainAssociation", http_method = "POST", 
        http_path = "/apps/{appId}/domains/{domainName}", paginator = list())
    input <- update_domain_association_input(appId = appId, domainName = domainName, 
        enableAutoSubDomain = enableAutoSubDomain, subDomainSettings = subDomainSettings)
    output <- update_domain_association_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}
