#' @param RequestParameters The request parameters for the route.
#' @param RouteId &#91;required&#93; The route ID.
#' @param RouteKey The route key for the route.
#' @param RouteResponseSelectionExpression The route response selection expression for the route.
#' @param Target The target for the route.
#'
#' @examples
#'
#' @export
update_route <- function (ApiId, ApiKeyRequired = NULL, AuthorizationScopes = NULL, 
    AuthorizationType = NULL, AuthorizerId = NULL, ModelSelectionExpression = NULL, 
    OperationName = NULL, RequestModels = NULL, RequestParameters = NULL, 
    RouteId, RouteKey = NULL, RouteResponseSelectionExpression = NULL, 
    Target = NULL) 
{
    op <- Operation(name = "UpdateRoute", http_method = "PATCH", 
        http_path = "/v2/apis/{apiId}/routes/{routeId}", paginator = list())
    input <- update_route_input(ApiId = ApiId, ApiKeyRequired = ApiKeyRequired, 
        AuthorizationScopes = AuthorizationScopes, AuthorizationType = AuthorizationType, 
        AuthorizerId = AuthorizerId, ModelSelectionExpression = ModelSelectionExpression, 
        OperationName = OperationName, RequestModels = RequestModels, 
        RequestParameters = RequestParameters, RouteId = RouteId, 
        RouteKey = RouteKey, RouteResponseSelectionExpression = RouteResponseSelectionExpression, 
        Target = Target)
    output <- update_route_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}

