#' Updates a Stage
#'
#' Updates a Stage.
#'
#' @usage
#' update_stage(
#'   AccessLogSettings = list(
#'     DestinationArn = "string",
#'     Format = "string"
#'   ),
#'   ApiId = "string",
#'   ClientCertificateId = "string",
#'   DefaultRouteSettings = list(
#'     DataTraceEnabled = TRUE|FALSE,
#'     DetailedMetricsEnabled = TRUE|FALSE,
#'     LoggingLevel = "ERROR"|"INFO"|"false",
#'     ThrottlingBurstLimit = 123,
#'     ThrottlingRateLimit = 123.0
#'   ),
#'   DeploymentId = "string",
#'   Description = "string",
#'   RouteSettings = list(
#'     list(
#'       DataTraceEnabled = TRUE|FALSE,
#'       DetailedMetricsEnabled = TRUE|FALSE,
#'       LoggingLevel = "ERROR"|"INFO"|"false",
#'       ThrottlingBurstLimit = 123,
#'       ThrottlingRateLimit = 123.0
#'     )
#'   ),
#'   StageName = "string",
#'   StageVariables = list(
#'     "string"
#'   )
#' )
#'
#' @param AccessLogSettings Settings for logging access in this stage.
#' @param ApiId &#91;required&#93; The API identifier.
#' @param ClientCertificateId The identifier of a client certificate for a Stage.
#' @param DefaultRouteSettings The default route settings for the stage.
#' @param DeploymentId The deployment identifier for the API stage.
#' @param Description The description for the API stage.
#' @param RouteSettings Route settings for the stage.
#' @param StageName &#91;required&#93; The stage name.
#' @param StageVariables A map that defines the stage variables for a Stage. Variable names can have alphanumeric and underscore characters, and the values must match &#91;A-Za-z0-9-.\_\~:/?\#&=,&#93;+.
#'
#' @examples
#'
#' @export
update_stage <- function (AccessLogSettings = NULL, ApiId, ClientCertificateId = NULL, 
    DefaultRouteSettings = NULL, DeploymentId = NULL, Description = NULL, 
    RouteSettings = NULL, StageName, StageVariables = NULL) 
{
    op <- Operation(name = "UpdateStage", http_method = "PATCH", 
        http_path = "/v2/apis/{apiId}/stages/{stageName}", paginator = list())
    input <- update_stage_input(AccessLogSettings = AccessLogSettings, 
        ApiId = ApiId, ClientCertificateId = ClientCertificateId, 
        DefaultRouteSettings = DefaultRouteSettings, DeploymentId = DeploymentId, 
        Description = Description, RouteSettings = RouteSettings, 
        StageName = StageName, StageVariables = StageVariables)
    output <- update_stage_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}
