put_scaling_policy <- function (PolicyName, ServiceNamespace, 
    ResourceId, ScalableDimension, PolicyType = NULL, StepScalingPolicyConfiguration = NULL, 
    TargetTrackingScalingPolicyConfiguration = NULL) 
