#' -   `DDB` for Amazon DynamoDB
#' 
#' -   `EFS` for Amazon Elastic File System
#'
#' @export
start_restore_job <- function (RecoveryPointArn, Metadata, IamRoleArn, 
    IdempotencyToken = NULL, ResourceType = NULL) 
{
    op <- new_operation(name = "StartRestoreJob", http_method = "PUT", 
        http_path = "/restore-jobs", paginator = list())
    input <- start_restore_job_input(RecoveryPointArn = RecoveryPointArn, 
        Metadata = Metadata, IamRoleArn = IamRoleArn, IdempotencyToken = IdempotencyToken, 
        ResourceType = ResourceType)
    output <- start_restore_job_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}

#' Attempts to cancel a job to create a one-time backup of a resource
