set_task_status <- function (taskId, taskStatus, errorId = NULL, 
    errorMessage = NULL, errorStackTrace = NULL) 
{
    op <- new_operation(name = "SetTaskStatus", http_method = "POST", 
        http_path = "/", paginator = list())
    input <- set_task_status_input(taskId = taskId, taskStatus = taskStatus, 
        errorId = errorId, errorMessage = errorMessage, errorStackTrace = errorStackTrace)
    output <- set_task_status_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}

#' Validates the specified pipeline definition to ensure that it is well formed and can be run without error
#'
#' Validates the specified pipeline definition to ensure that it is well formed and can be run without error.
#'
#' @section Accepted Parameters:
#' ```
#' validate_pipeline_definition(
#'   pipelineId = "string",
#'   pipelineObjects = list(
#'     list(
#'       id = "string",
#'       name = "string",
#'       fields = list(
#'         list(
#'           key = "string",
#'           stringValue = "string",
#'           refValue = "string"
#'         )
#'       )
#'     )
#'   ),
#'   parameterObjects = list(
#'     list(
#'       id = "string",
#'       attributes = list(
#'         list(
#'           key = "string",
#'           stringValue = "string"
#'         )
#'       )
#'     )
#'   ),
#'   parameterValues = list(
#'     list(
#'       id = "string",
#'       stringValue = "string"
#'     )
#'   )
#' )
#' ```
#'
#' @param pipelineId &#91;required&#93; The ID of the pipeline.
#' @param pipelineObjects &#91;required&#93; The objects that define the pipeline changes to validate against the pipeline.
#' @param parameterObjects The parameter objects used with the pipeline.
#' @param parameterValues The parameter values used with the pipeline.
#'
#' @export
validate_pipeline_definition <- function (pipelineId, pipelineObjects, 
    parameterObjects = NULL, parameterValues = NULL) 
{
    op <- new_operation(name = "ValidatePipelineDefinition", 
        http_method = "POST", http_path = "/", paginator = list())
    input <- validate_pipeline_definition_input(pipelineId = pipelineId, 
        pipelineObjects = pipelineObjects, parameterObjects = parameterObjects, 
        parameterValues = parameterValues)
    output <- validate_pipeline_definition_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}
