#'       DeviceName = "string",
#'       VirtualName = "string",
#'       Ebs = list(
#'         DeleteOnTermination = TRUE|FALSE,
#'         Iops = 123,
#'         SnapshotId = "string",
#'         VolumeSize = 123,
#'         VolumeType = "standard"|"io1"|"gp2"|"sc1"|"st1",
#'         Encrypted = TRUE|FALSE,
