#'   assessmentRunArn = "arn:aws:inspector:us-west-2:123456789012:target/0-nvgVhaxX/template/0-it5r2S4T/run/0-11LMTAVe"
#' )
#'
#' @export
stop_assessment_run <- function (assessmentRunArn, stopAction = NULL) 
{
    op <- Operation(name = "StopAssessmentRun", http_method = "POST", 
        http_path = "/", paginator = list())
    input <- stop_assessment_run_input(assessmentRunArn = assessmentRunArn, 
        stopAction = stopAction)
    output <- stop_assessment_run_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}

#' Enables the process of sending Amazon Simple Notification Service (SNS) notifications about a specified event to a specified SNS topic
#'
#' Enables the process of sending Amazon Simple Notification Service (SNS) notifications about a specified event to a specified SNS topic.
#'
#' @usage
#' subscribe_to_event(
#'   resourceArn = "string",
#'   event = "ASSESSMENT_RUN_STARTED"|"ASSESSMENT_RUN_COMPLETED"|"ASSESSMENT_RUN_STATE_CHANGED"|"FINDING_REPORTED"|"OTHER",
#'   topicArn = "string"
#' )
#'
#' @param resourceArn &#91;required&#93; The ARN of the assessment template that is used during the event for which you want to receive SNS notifications.
#' @param event &#91;required&#93; The event for which you want to receive SNS notifications.
#' @param topicArn &#91;required&#93; The ARN of the SNS topic to which the SNS notifications are sent.
#'
#' @examples
#' # Enables the process of sending Amazon Simple Notification Service (SNS)
#' # notifications about a specified event to a specified SNS topic.
#' subscribe_to_event(
#'   event = "ASSESSMENT_RUN_COMPLETED",
#'   resourceArn = "arn:aws:inspector:us-west-2:123456789012:target/0-nvgVhaxX/template/0-7sbz2Kz0",
#'   topicArn = "arn:aws:sns:us-west-2:123456789012:exampletopic"
#' )
#'
#' @export
subscribe_to_event <- function (resourceArn, event, topicArn) 
{
    op <- Operation(name = "SubscribeToEvent", http_method = "POST", 
        http_path = "/", paginator = list())
    input <- subscribe_to_event_input(resourceArn = resourceArn, 
        event = event, topicArn = topicArn)
    output <- subscribe_to_event_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}

#' Disables the process of sending Amazon Simple Notification Service (SNS) notifications about a specified event to a specified SNS topic
#'
#' Disables the process of sending Amazon Simple Notification Service (SNS) notifications about a specified event to a specified SNS topic.
#'
#' @usage
#' unsubscribe_from_event(
#'   resourceArn = "string",
#'   event = "ASSESSMENT_RUN_STARTED"|"ASSESSMENT_RUN_COMPLETED"|"ASSESSMENT_RUN_STATE_CHANGED"|"FINDING_REPORTED"|"OTHER",
#'   topicArn = "string"
#' )
#'
#' @param resourceArn &#91;required&#93; The ARN of the assessment template that is used during the event for which you want to stop receiving SNS notifications.
#' @param event &#91;required&#93; The event for which you want to stop receiving SNS notifications.
#' @param topicArn &#91;required&#93; The ARN of the SNS topic to which SNS notifications are sent.
#'
#' @examples
#' # Disables the process of sending Amazon Simple Notification Service (SNS)
