#' unsubscribe_from_event(
#'   event = "ASSESSMENT_RUN_COMPLETED",
#'   resourceArn = "arn:aws:inspector:us-west-2:123456789012:target/0-nvgVhaxX/template/0-7sbz2Kz0",
#'   topicArn = "arn:aws:sns:us-west-2:123456789012:exampletopic"
#' )
#'
#' @export
unsubscribe_from_event <- function (resourceArn, event, topicArn) 
{
    op <- Operation(name = "UnsubscribeFromEvent", http_method = "POST", 
        http_path = "/", paginator = list())
    input <- unsubscribe_from_event_input(resourceArn = resourceArn, 
        event = event, topicArn = topicArn)
    output <- unsubscribe_from_event_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}

#' Updates the assessment target that is specified by the ARN of the assessment target
#'
#' Updates the assessment target that is specified by the ARN of the assessment target.
#' 
#' If resourceGroupArn is not specified, all EC2 instances in the current AWS account and region are included in the assessment target.
#'
#' @usage
#' update_assessment_target(
#'   assessmentTargetArn = "string",
#'   assessmentTargetName = "string",
#'   resourceGroupArn = "string"
#' )
#'
#' @param assessmentTargetArn &#91;required&#93; The ARN of the assessment target that you want to update.
#' @param assessmentTargetName &#91;required&#93; The name of the assessment target that you want to update.
#' @param resourceGroupArn The ARN of the resource group that is used to specify the new resource group to associate with the assessment target.
#'
#' @examples
#' # Updates the assessment target that is specified by the ARN of the
#' # assessment target.
#' update_assessment_target(
#'   assessmentTargetArn = "arn:aws:inspector:us-west-2:123456789012:target/0-nvgVhaxX",
#'   assessmentTargetName = "Example",
#'   resourceGroupArn = "arn:aws:inspector:us-west-2:123456789012:resourcegroup/0-yNbgL5Pt"
#' )
#'
#' @export
update_assessment_target <- function (assessmentTargetArn, assessmentTargetName, 
    resourceGroupArn = NULL) 
{
    op <- Operation(name = "UpdateAssessmentTarget", http_method = "POST", 
        http_path = "/", paginator = list())
    input <- update_assessment_target_input(assessmentTargetArn = assessmentTargetArn, 
        assessmentTargetName = assessmentTargetName, resourceGroupArn = resourceGroupArn)
    output <- update_assessment_target_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}
