#' @param GroupByAttribute The updated GroupBy attribute that defines this insight.
#'
#' @export
update_insight <- function (InsightArn, Name = NULL, Filters = NULL, 
    GroupByAttribute = NULL) 
{
    op <- new_operation(name = "UpdateInsight", http_method = "PATCH", 
        http_path = "/insights/{InsightArn+}", paginator = list())
    input <- update_insight_input(InsightArn = InsightArn, Name = Name, 
        Filters = Filters, GroupByAttribute = GroupByAttribute)
    output <- update_insight_output()
    svc <- service()
    request <- new_request(svc, op, input, output)
    response <- send_request(request)
    return(response)
}
